import React, {useState} from 'react';
import ReactDataGrid from 'react-data-grid';

const RoomInputGrid = () => {
    const columns = [
        { key: "material", name: "Material", editable: true },
        { key: "area", name: "Area (m^2)", editable: true },
    ];

    const rows = [
        { material: "Brick", area: 24},
        { material: "Concrete", area: 9},
        { material: "Plaster", area: 9}
    ];

    const [tableData, updateTable] = useState({rows});

    const onGridRowsUpdated = ({ fromRow, toRow, updated }) => {
        updateTable(state => {
          const rows = tableData.rows.slice();
          for (let i = fromRow; i <= toRow; i++) {
            rows[i] = { ...rows[i], ...updated };
          }
          return { rows };
        });
    };

    const addMaterial = () => {
        updateTable(state => {
            state.rows.push({ material: "Plaster", area: 9});
            return state
        });
    }

    return (
        <>
        <h4 className="center">Room Construction</h4>
            <div className="row center">
                <div className="col s12 ">
                    <ReactDataGrid
                    columns={columns}
                    rowGetter={i => tableData.rows[i]}
                    rowsCount={tableData.rows.length}
                    onGridRowsUpdated={onGridRowsUpdated}
                    enableCellSelect={true}
                    />
                </div>
                <div className="col s12">
                    <a className="btn" onClick={addMaterial}>Add Material</a>
                </div>
            </div>
        </>

    );
};

export default RoomInputGrid;
