import React, {useState} from 'react';
import ReactDataGrid from 'react-data-grid';

const RoomInputGrid = () => {
    const columns = [
        { key: "material", name: "Material", editable: true },
        { key: "area", name: "Title", editable: true },
        { key: "complete", name: "Complete", editable: true }
    ];

    const rows = [
        { material: 0, area: "Task 1"},
        { material: 1, area: "Task 2"},
        { material: 2, area: "Task 3"}
    ];

    const [tableData, updateTable] = useState({rows});

    const onGridRowsUpdated = ({ fromRow, toRow, updated }) => {
        updateTable(state => {
          const rows = tableData.rows.slice();
          for (let i = fromRow; i <= toRow; i++) {
            rows[i] = { ...rows[i], ...updated };
          }
          return { rows };
        });
    };

    return (
        <>
        <h1 className="center">Room Config</h1>
            <div className="row center">
                <div className="col s12 ">
                    <ReactDataGrid
                    columns={columns}
                    rowGetter={i => tableData.rows[i]}
                    rowsCount={rows.length}
                    onGridRowsUpdated={onGridRowsUpdated}
                    enableCellSelect={true}
                    />
                </div>
                <div className="col s12">
                    <a className="btn">AMAZING</a>
                </div>
            </div>
        </>

    );
};

export default RoomInputGrid;
