import React, {useState} from 'react';
import LineGraph  from './components/LineGraph';
import RoomInputGrid from './components/RoomInputGrid';
import MaterialGrid from './components/MaterialsGrid';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const App = () => {

    const initialData = [
        { Material: "Brick", Area: 24},
        { Material: "Concrete", Area: 9},
        { Material: "Plaster", Area: 9},
    ];

    const [plotData, setPlot] = useState(RoomSolver(initialData, 18));

    const UpdatePlot = (data) => {
        setPlot(RoomSolver(data, volume));
    };

    return (
        <>
            <Grid container spacing={3} justify="center">
                <Grid item xs={10}>
                    <MaterialGrid />
                </Grid>
                <Grid item xs={12} align="center">
                    <TextField
                        id="standard-basic"
                        label="Volume (m^3)"
                        value={volume}
                        onChange={(event) => {
                            setVolume(event.target.value);
                        }}
                    />
                </Grid>
                <Grid item xs={10}>
                    <RoomInputGrid updatePlot={UpdatePlot} data={initialData}/>
                </Grid>
                <Grid item xs={12} align="center">
                    <LineGraph data={plotData}/>
                </Grid>
            </Grid>
        </>
    );
};


export default App;
