import React, {useState} from 'react';
import MaterialTable from 'material-table';

const RoomInputGrid = (props) => {
    const [tableData, setData] = useState([
        { Material: "Concrete", Area: 22}
    ]);

    const AddRow = (data) => {
        setData({...data});
    }

    return (
        <MaterialTable
          columns={[
            { title: "Material", field: "Material" },
            { title: "Area (M^2)", field: "Area", type: "numeric" },
          ]}
          data={tableData}
          title="Room Construction"
          editable={{
            onRowAdd: newData => { return new Promise((resolve, reject) => {
                let data = tableData;
                data.push(newData);
                AddRow(data);
                resolve();
            }); },
            onRowUpdate: (newData, oldData) =>{return new Promise((resolve, reject) => {
                //setData(newData);
                resolve();
            }); },
            onRowDelete: oldData => {return new Promise((resolve, reject) => {
                resolve();
            }); }

        }}
        />
    );
};

export default RoomInputGrid;
